import React from 'react'

import RespondComponent from './respond.component'

class Respond_Bottom extends React.Component {
    constructor(props) {
        super(props);
        this.getBackOnRequestList = this.getBackOnRequestList.bind(this);
    }
    
    getBackOnRequestList(e){
        this.props.stateChanger({screen_id:'1', sub_screen:'1'});
    }

    render() {
        let content;
        let itemsCount = this.props.items;
        if(itemsCount.length == 0){
            content =   
                (<div className="mt-4">
                    <h5 class="">Пока никто не откликнулся на ваш запрос :(</h5>
                    <p class="text-muted m-0 mt-4">Зайдите немного позже, и кто-нибудь вам точно поможет</p>
                    <button class="mx-auto purp-button btn p-2 px-5" onClick={this.getBackOnRequestList}>К списку запросов</button>
                </div>)
        }
        else if(itemsCount.length > 0){
            if(itemsCount[0].request_responder!=0){
               content = <RespondComponent type="0" item={this.props.items[0]} stateChanger={this.props.stateChanger} />
            }
            else content = (
                    <React.Fragment>
                        <h3 className="my-4 offset-2">Эти люди готовы помочь вам</h3>
                        {this.props.items.map(item => {
                            return <RespondComponent type="1" item={this.props.item} stateChanger={this.props.stateChanger} />
                        })}
                    </React.Fragment>)
        }

        return(
            {content}   
        )
    }
}

export default Respond_Bottom
