import React from 'react'

import UserBrief from '../../../common_components/user_brief'

import phone from '../../../../img/phone-call.svg'
import marker from '../../../../img/marker.svg'

class RespondComponent extends React.Component {
    constructor(props){
        super(props);
        this.DeclineButton = this.DeclineButton.bind(this);
    }
    DeclineButton(e){
        let request_id = e.target.parentNode.dataset.id;
        this.props.stateChanger({screen_id:"2", sub_screen: "1",request_id: request_id, state: "0"});
    }

    ConfirmButton(e){
        let request_id = e.target.parentNode.dataset.id;
        this.props.stateChanger({screen_id:"2", sub_screen: "1",request_id: request_id, responder: this.props.item.responder,state:"1"});
    }

    render(){
        let buttonClock;
        if(this.props.type == "0"){
            buttonClock = (
                <button class="btn p-2 px-5 mr-3 decline-button" onClick={this.DeclineButton}>Отказать</button>
            )
        }
        else buttonClock = (
            <React.Fragment>
                <button class="btn p-2 px-5 mr-3 decline-button" onClick={this.DeclineButton}>Отказать</button>
                <button class="btn p-2 px-5 purp-button " onClick={this.ConfirmButton}>Выбрать</button>
            </React.Fragment>
            )
        return(
            <main class="d-flex flex-column mx-auto col-8 p-2  mt-3 rounded bg-white">
                <div class="mx-auto col-10">

                    <div class="d-flex flex-sm-row justify-content-between my-4">
                        <UserBrief user={this.props.item.responder}/>
                        <div class="R-side d-flex flex-column justify-content-between">
                            <div class="">
                                <img src={phone} width="15"/>
                                <a href="$" class="ml-2">{this.props.item.phone}</a>
                            </div>
                            <div class="">
                                <img src={marker} width="15"/>
                                <span class="text-muted ml-2">{this.props.item.location}</span>
                            </div>
                        </div>
                    </div>

                    <hr/>

                    <p class="text-muted m-0 mt-4">Цена:</p>
                    <h1>{this.props.item.price} Р</h1>
                    <p class="text-muted m-0 mt-4">Описание:</p>
                    <p>{this.props.item.description}</p>
                </div>
                <div class="d-flex justify-content-end pb-2 pr-5" data-id={this.props.item.request_id}>
                {buttonClock}
                </div>
            </main>
        )
    }
}

export default RespondComponent;
