import React from 'react'

import AboutUser from '../aboutUser.component'
import Map from '../../../common_components/Map'
import properties from '../../../../img/properties.svg'
import downArrow from '../../../../img/arrowDown.svg'
import marker from '../../../../img/marker.svg'

class RequestAnswer extends React.Component {
    dropDownNameChange(){
        let text = document.getElementById('dropDownText')
        if(document.getElementById('map').classList.contains('show')) text.textContent = "Скрыть";
        else text.textContent = "Показать больше";
    }

    render() {
        let hideMap;
        let user_data;
        let show = 'd-block collapse';
        //На втором экране усеченный верх
        if (this.props.screen_id == "1"){
            hideMap =   <a href="#map" data-toggle="collapse" role="button" aria-expanded="false" aria-controls="map" className='container-fluid purp-back text-center' onClick={this.dropDownNameChange}>
                            <span id="dropDownText">Показать больше</span>
                            <img className="ml-2" src={downArrow}/>
                        </a>

            user_data = <div class="">
                            <img src={marker} width="15"/>
                            <span class="text-muted ml-2">{this.props.data.location}</span>
                        </div>
        }
        else {  user_data= <AboutUser  
                            username={this.props.data.username}
                            photo={this.props.data.photo}
                            phone={this.props.data.telephone}
                            location={this.props.data.request_location}/>
                show = 'd-block collapse show';}
        return (
            <main class="d-flex flex-column mx-auto col-8 p-0  mt-3 rounded bg-white">
                <a href="#"  class="ml-auto mt-4 mr-4">
                    <img src={properties}/>
                </a>

                <div class="mx-auto col-10">
                    <h5 class="">{this.props.data.request_title}</h5>
                    <p class="text-muted m-0 mt-4">Описание:</p>
                    <p>{this.props.data.request_description}</p>
                    <p class="text-muted m-0 mt-4">Даты:</p>
                    <p class="font-weight-bold">{this.props.data.request_whenDate} - {this.props.data.request_tillDate}</p>
                    <p class="text-muted m-0 mt-4">Рекомендованная цена:</p>
                    <p class="font-weight-bold">{this.props.data.request_price}Р</p>
                    {user_data}
                </div>
                {hideMap}
                <legend id="map" className={show}>
                    <Map placemark={[55.7578993,37.6582756]}/>
                </legend>
            </main>
        )
    }
}

export default RequestAnswer
