import React from 'react'

class Respond_Bottom extends React.Component {
    constructor(props) {
        super(props);
        this.getBackOnRequestList = this.getBackOnRequestList.bind(this);
        this.saveAnswer = this.saveAnswer.bind(this);
    }
    
    getBackOnRequestList(e){
        e.preventDefault();
        this.props.respondingChange({responding:"false"});
    }
    
    saveAnswer() {
        let price = document.getElementById('answer_price');
        let description = document.getElementById('answer_description');
        let data = {request_id:this.props.items.request_id, price: price.value, description:description.value};
        this.props.respondingChange({screen_id:'1', sub_screen:'0', answer_data: data});
    }

    render() {
        return(
            <React.Fragment>
                <div class="mx-auto col-10">
                    <form class="d-flex flex-column col-12 p-5">
                        <span class="mb-2">Цена:</span>
                        <div class="mb-4">
                            <input type="number" class="searchbar" placeholder="1000" id="answer_price"/>
                            <img src={ruble}/>
                        </div>
                    

                        <span class="mb-2">Описание:</span>
                        <textarea name="description" id="answer_description" class="textarea mb-4 rounded p-3" rows="5" placeholder="Платье шуруповерт супер"></textarea>

                        <span class="mb-2">Фото:</span>
                        <div class="drag d-flex flex-column align-items-center justify-content-center rounded">
                            <p class="text-muted mb-3">Добавить фотографии</p>
                            <img src={crop}/>
                        </div>
                    </form>
                </div>
                <div class="d-flex justify-content-end offset-7 col-3 py-4 pr-0">
                    <a href="#" class="my-auto mr-4 refs" onClick={this.getBackOnRequestList}>Отменить</a>
                    <button class="purp-button btn p-2 px-5" onClick={this.saveAnswer}>Откликнуться</button>
                </div>
            </React.Fragment>
        )
    }
}
