import React from 'react';

import star from '../../img/star.svg';
import halfStar from '../../img/star_half.svg';
import avatar from '../../img/avatar-girl.svg'; 

class UserBrief extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }

    componentDidMount() {
        let formData = new FormData();
        formData.append('user_id', this.props.user);
        let dataToSend = {  method: "POST",
                            body:formData
                        };
        fetch("/react-common/my-app/src/screens/common_components/php/user_brief.php", dataToSend)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        item: result
                    });
                },
                (error) => {
                    this.setState({
                    isLoaded: true,
                    error
                    });
                }
            )
    }
    render(){
        const { error, isLoaded, item } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <div className="LB-side d-flex justify-content-center">
                    <img src={item[0].photo} className="rounded-circle" width="50" height="50"/>
                    <div className="ml-3">
                        <span>{item[0].username}</span>
                        <div className="rating-bar text-center">
                            <span className="mr-2 align-middle">4,3</span>
                            <img src={star}  width="15"/>
                            <img src={star}  width="15"/>
                            <img src={star}  width="15"/>
                            <img src={star}  width="15"/>
                            <img src={halfStar}  width="15"/>
                            <span className="text-muted">(126)</span>
                        </div>
                    </div>
                </div>
            )
        }
    }
}

export default UserBrief;
