import React from 'react'

import CommentBlock from '../../../common_components/commentBlock'
import Fetch from '../../../service/fetch'
import ruble from '../../../../img/ruble_sign.svg'
import crop from '../../../../img/crop_original.svg'

class Respond_Bottom extends React.Component {
    constructor(props) {
        super(props);
        this.cancelAnswer = this.cancelAnswer.bind(this);
    }
    
    cancelAnswer() {
        alert("Отмена отклика");
        //Восстановить
        // let data = {user_id:this.props.user_id, request_id:this.props.request_id};
        
        // Fetch.getData('\react-app-05\src\screens\content\respond\php\respondChanger.php', data)
        //     .then((result)=>{
        //         this.props.respondingChange({responding: 'false'})
        //     })
        //     .catch((error)=>alert("Ошибка записи отклика на запрос"));
    }

    render() {
        return(
            <React.Fragment>
                <main class="d-flex flex-column mx-auto col-8 p-2  mt-3 rounded bg-white">
                        <form class="d-flex flex-column col-12 p-5">
                            <span class="mb-2">Цена:</span>
                            <div class="mb-4">
                                <p className="strong">{this.props.items.price}</p>
                                <img src={ruble}/>
                            </div>
                            <span class="mb-2">Фото:</span>
                            <div class="drag d-flex flex-column align-items-center justify-content-center rounded">
                                <p class="text-muted mb-3">Фотографии</p>
                            </div>
                            <span class="mb-2">Описание:</span>
                            <p>{this.props.items.respond_text}</p>

                            {/* {CommentBlock} */}

                        </form>
                </main>
                <div class="d-flex justify-content-end offset-7 col-3 py-4 pr-0">
                    <button class="orange-button btn p-2 px-5" onClick={this.cancelAnswer}>Отказаться</button>
                </div>
            </React.Fragment>
        )
    }
}

export default Respond_Bottom
