import React from 'react'

import UserBrief from '../../common_components/user_brief'
import phone from '../../../img/phone-call.svg'
import marker from '../../../img/marker.svg'

class AboutUser extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        return (
            <div class="d-flex flex-sm-row justify-content-between my-4">
                <UserBrief username={props.username} photo={props.photo}/>
                <div class="R-side d-flex flex-column justify-content-between">
                    <div class="">
                        <img src={phone} width="15"/>
                        <a href="$" class="ml-2">{props.phone}</a>
                    </div>
                    <div class="">
                        <img src={marker} width="15"/>
                        <span class="text-muted ml-2">{props.location}</span>
                    </div>
                </div>
            </div>
        )
    }
}

export default AboutUser
