import React from 'react'

import star from '../../img/star.svg';
import halfStar from '../../img/star_half.svg';
import avatar from '../../img/avatar-man-1.svg'; 
import editPhoto from '../../img/edit photo.svg';

class Profile extends React.Component {
    constructor(props) {
        super(props);
        this.LeaveProfile = this.LeaveProfile.bind(this);
    }

    LeaveProfile(e) {
        e.preventDefault();
        this.props.stateChanger({screen_id:0, sub_screen:0, user_id: 0});
    }

    render() {
        return (
            <main class="mx-auto col-6  mt-3 rounded bg-white">
                <form class="mx-auto col-8 col-md-6 col-xl-6 p-4 needs-validation">
                    <div class="avatar text-center">
                            <img class="rounded-circle" src={avatar} alt="Avatar" width="140" height="140"/>
                            <img src={this.props.items.photo} class="under-avatar"  width="50"/>
                    </div>

                    <div class="rating-bar text-center mt-3">
                        <span class="mr-2 align-middle">4,3</span>
                        <img src={star} width="20" height="20"/>
                        <img src={star} width="20" height="20"/>
                        <img src={star}  width="20" height="20"/>
                        <img src={star} width="20" height="20"/>
                        <img src={halfStar} width="20" height="20"/>
                    </div>

                    <div class="text-center text-wrap mt-1">
                            Заказов выполнено: <b>126</b>
                    </div>

                    <p class="text-muted m-0 mt-4">Имя</p>
                    <p class="text-field px-0">{this.props.items.user_name}</p>

                    <p class="text-muted m-0 mt-4">Телефон</p>
                    <p class="text-field px-0">{this.props.items.phone}</p>

                    <p class="text-muted m-0 mt-4">Email</p>
                    <p class="text-field px-0">{this.props.items.email}</p>

                    <p class="text-muted m-0 mt-4">Город</p>
                    <p class="text-field px-0 m-0">{this.props.items.location}</p>

                    <div class="d-flex flex-column text-center my-4">
                        <a class="">Изменить пароль</a>
                        <a class="text-danger" onClick={this.LeaveProfile}>Покинуть профиль</a>
                    </div>
                </form>
            </main>
        )
    }
}

export default Profile
