class DataSend{
    static getData(path, data){
        let dataToSend ={
                            method: "POST",
                            body:JSON.stringify(data)
                        };
        fetch(path, dataToSend)
            .then(res => res.json())
            .then((result) => ({isLoaded: true, items: result}),
                  (error) => ({isLoaded: true, error}))
            }
}

export default DataSend;
