import React from 'react'

class DataSend extends React.Component{
    static getData(path, data){
        let dataToSend ={
                            method: "POST",
                            body:JSON.stringify(data)
                        };
        fetch(path, dataToSend)
            .then(res => res.json())
            .then((result) => {
                        this.setState({
                            isLoaded: true,
                            item: result
                        });
                    },
                    (error) => {
                        this.setState({
                        isLoaded: true,
                        error
                        });
                    })
            }
}

export default DataSend;
