class DataSend{
    static getData(path, data){
        let dataToSend ={
                            method: "POST",
                            body:JSON.stringify(data)
                        };
        return fetch(path, dataToSend)
            .then(res => res.json())
            .then(function(result) {return {isLoaded: true, items: result};})
            .catch(function(error) {return {isLoaded: true, error};})
    }
}

export default DataSend;
