import React from 'react'
import ReactDOM from 'react-dom'

import CreationFirstScreen from './CreationFirst.part'
import CreationSecondScreen from './CreationSecond.part'
import CreationThirdScreen from './CreationThird.part'

import RequestList from '../request_list/request_list.screen'

class RequestCreationContainer extends React.Component {
    constructor() {
        super();
        this.HandleCategorySelect = this.HandleCategorySelect.bind(this);
        this.HandleCategoryTitle = this.HandleCategoryTitle.bind(this);
        this.HandleCategoryDate = this.HandleCategoryDate.bind(this);
        this.state = {
            screen_position: 0
        };

        this.request= {
            category_id: 0,
            category_name: false,
            title: false,
            price: false,
            description: false,
            whenDate: false,
            tillDate: false,
            location: false
        }
        this.user_id = 9;
    }

    HandleCategorySelect(id, name) {
        this.setState({screen_position: 1});
        this.request.category_id = id;
        this.request.category_name = name;
    }

    HandleCategoryTitle(category_title, category_description, category_price) {
        this.setState({screen_position: 2});
        this.request.title= category_title;
        this.request.price = category_price;
        this.request.description=category_description;
    }

    HandleCategoryDate(category_whenDate, category_tillDate, category_location){

        this.request.whenDate = category_whenDate;
        this.request.tillDate = category_tillDate;
        this.request.location = category_location;

        let formData = new FormData();
        formData.append('user_id', this.user_id);
        formData.append('category', this.request.category_id);
        formData.append('title', this.request.title);
        formData.append('price',this.request.price);
        formData.append('whenDate',this.request.whenDate);
        formData.append('tillDate',this.request.tillDate);
        formData.append('location',this.request.location);
        formData.append('description', this.request.description);

        let dataToSend = {  method: "POST",
                            body:formData
                        };
        fetch("/react-common/my-app/src/screens/request_creation/php/request_creation.php", dataToSend)
            .then(res => res.text())
            .then((result) => {
                alert(result);
                }
            )

        // alert(  "ID категории: " + this.request.category_id +
        //         "\nНазвание категории: " + this.request.category_name +
        //         "\nИмя запроса: " + this.request.title +
        //         "\nЦена:" + this.request.price +
        //         "\nОписание: " + this.request.description + 
        //         "\nКогда: " + this.request.whenDate + 
        //         "\nДо какого: " + this.request.tillDate + 
        //         "\nГде: " + this.request.location);

        ReactDOM.render(<RequestList />, document.getElementById('content'));
    }

    render() {
        switch(this.state.screen_position){
            case 0: 
                return <CreationFirstScreen 
                        onCategorySelect = {this.HandleCategorySelect}/>
            case 1:
                return <CreationSecondScreen 
                        onTitleSelect = {this.HandleCategoryTitle}/>
            case 2:
                return <CreationThirdScreen 
                        onDateSelect = {this.HandleCategoryDate}/>
        }
    }
}

export default RequestCreationContainer
