import React from 'react'

import Category from './category/category'
import Filter from './center/filter_top'
import RequestComponent from '../../common_components/request_component'
import Loading from '../../common_components/loading'
import Fetch from '../../service/fetch'

import '../../../css/simple-sidebar.css';

class RequestList extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            selected_category:"1",
            error: null,
            isLoaded: false,
            items: []
        };
        this.categoryChange = this.categoryChange.bind(this);
        this.handlerRespondingChange = this.handlerRespondingChange.bind(this);
    }

    categoryChange(data){
        this.setState(data);
    }

    handlerRespondingChange(data){ 
        if(this.props.screen_id == "2"){

            let path = "react-app-03/src/screens/content/request_list/php/deleteRespond.php";
            //удаление 'отклика' из БД
            let dataToSend = {user_id: this.props.user_id, request_id: data.request_id};
            Fetch.getData(path, dataToSend)
            .then((result) => alert('Успешное удаление отклика'))
            .catch((error) => {alert(error);return;});
            
            //удаление 'отклика' из массива
            let arr = this.prevState.items;
            for(let i=0; i<arr.length; i++){
                if(data.request_id == arr[i].request_id){
                    arr.splice(i,1);
                }
            }
            this.setState({items: arr});
        }
        else this.props.respondingChange(data);
    }

    componentDidMount() {
        // let path;
        // switch (this.props.screen_id){
        //     case "0":
        //         path='react-app-03/src/screens/content/request_list/php/requests.php';
        //         break;
        //     case "1":
        //         path='react-app-03/src/screens/content/request_list/php/myRequests.php';
        //         break;
        //     case "2":
        //         path='react-app-03/src/screens/content/request_list/php/myResponds.php';
        //         break;
        // }

        // Fetch.getData(path, {user_id: this.props.user_id})
        // .then((result) => this.setState({isLoaded: true, items: result}))
        // .catch((error) => this.setState({isLoaded: true, error}))
    }

    componentDidUpdate(prevProps) {
        let path;
        alert(prevProps.screen_id)
        if(this.props.screen_id != prevProps.screen_id){
            switch (this.props.screen_id){
                case "0":
                    path='react-app-03/src/screens/content/request_list/php/requests.php';
                    break;
                case "1":
                    path='react-app-03/src/screens/content/request_list/php/myRequests.php';
                    break;
                case "2":
                    path='react-app-03/src/screens/content/request_list/php/myResponds.php';
                    break;
            }
    
            Fetch.getData(path, {user_id: this.props.user_id})
            .then((result) => this.setState({isLoaded: true, items: result}))
            .catch((error) => this.setState({isLoaded: true, error}))
        }
    }

    render() {
        let Sidebar, filter;
        if(this.props.screen_id == "0"){
            Sidebar = <Category categoryChange={this.categoryChange} selected_category={this.state.selected_category}/>
            filter  = <Filter stateChanger={this.props.stateChanger}/>;
        }

        const { error, isLoaded, item } = this.state;
        if (error) {
            return <div className="mx-auto mt-3">Ошибка: {error.message}</div>;
        } 
        else if (!isLoaded) {
            return <Loading />
        }
        else {
            return (
                <main className="mt-2" id="wrapper">
                    {Sidebar}
                    <div id="page-content-wrapper" className="d-flex">
                        <div className="center-field flex-fill">
                            {filter}
                            <div className="request-list">
                                {this.state.items.map(item => {
                                    return <RequestComponent key={item.request_id} type={this.props.screen_id}  data={item} stateChanger={this.props.stateChanger} handlerRespondingChange={this.handlerRespondingChange}/>
                                })}
                            </div>
                        </div>  
                        <div className="right-side d-none d-xl-block" style={{minWidth: 250}}></div>
                    </div>
                </main>
            )
        }
    }
}

export default RequestList
